Scripts.OnShow:
if RPSCoreActionCam then
    RPS_ACTIONCAMCheckBox:SetChecked(true);
    SetCVar("test_cameraDynamicPitch", 0.35);
    SetCVar("test_cameraOverShoulder", 0.35);
else
    RPS_ACTIONCAMCheckBox:SetChecked(false);
    SetCVar("test_cameraDynamicPitch", 0);
    SetCVar("test_cameraOverShoulder", 0);
end

Scripts.OnClick:
if self:GetChecked() then
    RPSCoreActionCam = true;
    SetCVar("test_cameraDynamicPitch", 0.35);
    SetCVar("test_cameraOverShoulder", 0.35);
else
    RPSCoreActionCam = false;
    SetCVar("test_cameraDynamicPitch", 0);
    SetCVar("test_cameraOverShoulder", 0);
end

Scripts.OnShow:
if (RPSCoreShowPOIPins and RPSCoreShowPOIPins ~= nil) then
    RPS_POIPinsCheckBox:SetChecked(true);
    RPSCoreShowPOIPins = true;
else
    RPS_POIPinsCheckBox:SetChecked(false);
    RPSCoreShowPOIPins = false;
end
RPSCoreFramework:GeneratePOIPlaces()

Scripts.OnClick:
if (self:GetChecked()) then
    RPSCoreShowPOIPins = true;
else
    RPSCoreShowPOIPins = false;
end
RPSCoreFramework:GeneratePOIPlaces()

Scripts.OnVerticalScroll:
if (RPSCoreFramework.CanScroll) then
    if (RPS_AuraButton6:IsShown()) then -- Костыль который катит телегу
        FauxScrollFrame_OnVerticalScroll(self, offset, 32, RPSCoreFramework:GenerateScrollMenu());
        FauxScrollFrame_OnVerticalScroll(self, offset, 32, RPSCoreFramework:GenerateScrollMenu());
    else
        FauxScrollFrame_OnVerticalScroll(self, offset, 32, RPSCoreFramework:GenerateScrollMenu());
    end
end

Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_CURSOR");
GameTooltip:AddDoubleLine("RPS Darkmoon:", "TestUnit", 1, 1, 1, 1, 0.96, 0.41);
GameTooltip:AddDoubleLine(" ", "-SeMGa!", 1, 1, 1, 1, 0.96, 0.41);
GameTooltip:AddDoubleLine("Версия:", "2.0 BFA", 1, 1, 1, 1, 0.96, 0.41);
GameTooltip:Show();
