Scripts.OnTextChanged:
if self:IsVisible() and self:GetCursorPosition() == string.len(self:GetText()) then
	self:GetParent():SetVerticalScroll(self:GetParent():GetVerticalScrollRange());
end

Scripts.OnEnter:
        	GameTooltip:SetOwner(self, "ANCHOR_CURSOR");
		    GameTooltip:AddLine("|cFFFFFFFFСоздать макрос|r");
		    GameTooltip:AddLine(" ");
		    GameTooltip:AddLine("Создаёт макрос и выбирает его.");
		    GameTooltip:Show();

        	GameTooltip:SetOwner(self, "ANCHOR_CURSOR");
		    GameTooltip:AddLine("|cFFFFFFFFИзбранное|r");
		    GameTooltip:AddLine(" ");
		    GameTooltip:AddLine("Добавить\\исключить ауру из избранного.");
		    GameTooltip:Show();

RPSPOIGameTooltip:SetOwner(self, "ANCHOR_CURSOR");
RPSPOIGameTooltip:AddLine("|T"..self.ntex:GetTexture()..":0|t ".."|cffFFC125"..self.dataName.."|r");
if (string.len(self.dataDescription) > 0) then
	if (string.len(self.dataDescription) > 80) then
		RPSCoreFramework.POIDesc = RPSCoreFramework:WordWrap(self.dataDescription, 90);
		for i=1, #RPSCoreFramework.POIDesc do
			RPSPOIGameTooltip:AddLine("|cff808080"..RPSCoreFramework.POIDesc[i].."|r");
		end
	else
		RPSPOIGameTooltip:AddLine("|cff808080"..self.dataDescription.."|r");
	end
end
if true then
	RPSPOIGameTooltip:AppendText(" |cff80528C("..self.debugGuid..")");
end
RPSPOIGameTooltip:Show();
RPSCoreFramework.POIDesc = {};

Scripts.OnEditFocusGained:
if self.disabled then
	self:ClearFocus();
else
	self:HighlightText();
end

Scripts.OnTextChanged:
if self:IsVisible() and self:GetCursorPosition() == string.len(self:GetText()) then
self:GetParent():SetVerticalScroll(self:GetParent():GetVerticalScrollRange());
end

if tonumber(self:GetText()) ~= nil or self:GetText() == "" then
	self:SetTextColor(0,1,0,1);
else
	self:SetTextColor(1,0,0,1);
end

Scripts.OnEditFocusLost:
local text = self:GetText();
if ( self.startName ~= text and text ~= "" and tonumber(self:GetText()) ~= nil and RPSCoreFramework:isvalid(tonumber(text))) then
	RPSCoreFramework.SalaryByRank[tostring(self:GetParent():GetID())] = tonumber(text);
	self:SetText(tonumber(text));
else
	if (self.startName ~= nil and self.startName ~= "") then
		self:SetText(self.startName);
	else
		self:SetText("0");
		RPSCoreFramework.SalaryByRank[tostring(self:GetParent():GetID())] = 0;
		self:SetTextColor(1, 1, 1, 1);
	end
end
self.startName = nil;
self:SetTextColor(1, 1, 1, 1);

Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
if ( self:IsEnabled() ) then
	GameTooltip:SetText("Нажмите для установки заданного значения заработной платы.");
elseif ( self.tooltip ) then
	GameTooltip:SetText(self.tooltip, nil, nil, nil, nil, true);
end

        	GameTooltip:SetOwner(self, "ANCHOR_CURSOR");
		    GameTooltip:AddLine("|TInterface\\AddOns\\RPSDarkmoon\\resources\\POI\\"..self.FuncTable[5]..":0|t "..self.FuncTable[7]);
		    GameTooltip:AddLine(self.FuncTable[8], 1,1,1, true);
		    GameTooltip:AddLine("|cFFFF8040Локация:|r "..GetRealZoneText(self.FuncTable[2]), 1,1,1, true);
		    GameTooltip:Show();
